import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [cityImage, setCityImage] = useState("");
  const [error, setError] = useState("");
  const [isCelsius, setIsCelsius] = useState(true);

  const fetchCityImage = async (cityName) => {
    try {
      const unsplashAccessKey = "S34fXCk3kF4x2wGTpEixKLVkVZWwtEcsh4lojX3Ubko";
      const response = await axios.get(
        `https://api.unsplash.com/search/photos`,
        {
          params: {
            query: `${cityName} skyline`,
            client_id: unsplashAccessKey,
            per_page: 1,
            orientation: "landscape",
          },
        }
      );
      setCityImage(response.data.results[0]?.urls.regular || "");
    } catch (err) {
      setCityImage("");
      console.error("Error fetching city image:", err);
    }
  };

  const fetchWeather = async () => {
    try {
      setError("");
      setWeather(null);
      setCityImage("");
      const apiKey = "79b5cdad33a13f8b96ec23e71c4b0930";
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather`,
        {
          params: {
            q: city,
            appid: apiKey,
            units: "metric",
          },
        }
      );
      setWeather(response.data);
      fetchCityImage(city);
    } catch (err) {
      setError("City not found. Please try again.");
      console.error("Error fetching weather data:", err);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
  };

  const getWeatherCondition = (main) => {
    const conditions = {
      Clear: "Clear sky",
      Clouds: "Cloudy",
      Rain: "Rainy",
      Drizzle: "Drizzle",
      Thunderstorm: "Thunderstorm",
      Snow: "Snowy",
      Mist: "Misty",
      Haze: "Hazy",
    };
    return conditions[main] || main;
  };

  const getBackgroundStyle = (main) => {
    const styles = {
      Clear: "#87CEEB", // Blue for clear sky
      Clouds: "#D3D3D3", // Gray for cloudy
      Rain: "#708090", // Dark gray for rain
      Snow: "#F0FFFF", // Light blue for snow
      Thunderstorm: "#778899", // Gray for thunderstorm
      Drizzle: "#AFEEEE", // Pale blue for drizzle
      Mist: "#F5F5F5", // Light gray for mist
      Haze: "#B0C4DE", // Light slate gray for haze
    };
    return styles[main] || "#FFFFFF"; // Default to white
  };

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        padding: "20px",
        backgroundColor: weather
          ? getBackgroundStyle(weather.weather[0].main)
          : "#FFFFFF",
        minHeight: "100vh",
      }}
    >
      <h1>Weather App</h1>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={{
            padding: "10px",
            marginRight: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        />
        <button
          onClick={fetchWeather}
          style={{
            padding: "10px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Get Weather
        </button>
      </div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {weather && (
        <div
          style={{
            marginTop: "20px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        >
          <h2>Weather in {weather.name}</h2>
          <img
            src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt={weather.weather[0].description}
            style={{ width: "100px", height: "100px" }}
          />
          <p>
            <strong>Temperature:</strong>{" "}
            {isCelsius
              ? `${weather.main.temp}째C`
              : `${(weather.main.temp * 9) / 5 + 32}째F`}
          </p>
          <p>
            <strong>Humidity:</strong> {weather.main.humidity}%
          </p>
          <p>
            <strong>Wind Speed:</strong> {weather.wind.speed} m/s
          </p>
          <p>
            <strong>Condition:</strong>{" "}
            {getWeatherCondition(weather.weather[0].main)}
          </p>
          <p>
            <strong>Description:</strong> {weather.weather[0].description}
          </p>
          <p>
            <strong>Sunrise:</strong> {formatTime(weather.sys.sunrise)}
          </p>
          <p>
            <strong>Sunset:</strong> {formatTime(weather.sys.sunset)}
          </p>
        </div>
      )}
      {cityImage && (
        <div>
          <img
            src={cityImage}
            alt={`A view of ${city}`}
            style={{
              width: "100%",
              height: "auto",
              marginTop: "20px",
              borderRadius: "5px",
            }}
          />
        </div>
      )}
      <button
        onClick={() => setIsCelsius(!isCelsius)}
        style={{
          padding: "5px 10px",
          marginTop: "10px",
          backgroundColor: "#007BFF",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Switch to {isCelsius ? "Fahrenheit" : "Celsius"}
      </button>
    </div>
  );
};

export default WeatherApp;
