import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const fetchWeather = async () => {
    try {
      setError("");
      const apiKey = "79b5cdad33a13f8b96ec23e71c4b0930";
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather`,
        {
          params: {
            q: city,
            appid: apiKey,
            units: "metric",
          },
        }
      );
      setWeather(response.data);
    } catch (err) {
      setError("City not found. Please try again.");
    }
  };

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        minHeight: "100vh",
        backgroundColor: "#f4f4f4",
      }}
    >
      {/* Navigation Bar */}
      <nav
        style={{ backgroundColor: "#007BFF", padding: "10px", color: "white" }}
      >
        <h1 style={{ margin: "0", display: "inline" }}>Weather App</h1>
        <div style={{ float: "right" }}>
          <a href="#" style={{ color: "white", marginRight: "10px" }}>
            Home
          </a>
          <a href="#" style={{ color: "white", marginRight: "10px" }}>
            Forecast
          </a>
          <a href="#" style={{ color: "white" }}>
            Radar
          </a>
        </div>
      </nav>

      {/* Search Section */}
      <div
        style={{
          textAlign: "center",
          padding: "20px",
          backgroundImage:
            "url('https://source.unsplash.com/1600x900/?clouds')",
          backgroundSize: "cover",
          color: "white",
        }}
      >
        <h2>Search for Weather in Your City</h2>
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={{
            padding: "10px",
            marginRight: "10px",
            borderRadius: "5px",
            border: "none",
            width: "200px",
          }}
        />
        <button
          onClick={fetchWeather}
          style={{
            padding: "10px 20px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Get Weather
        </button>
      </div>

      {/* Main Content */}
      <div style={{ display: "flex", padding: "20px" }}>
        {/* Left Section: Weather Details */}
        <div style={{ flex: "2", padding: "10px" }}>
          {error && <p style={{ color: "red" }}>{error}</p>}
          {weather && (
            <div
              style={{
                backgroundColor: "white",
                padding: "20px",
                borderRadius: "5px",
                boxShadow: "0px 0px 10px rgba(0,0,0,0.1)",
              }}
            >
              <h2>Weather in {weather.name}</h2>
              <p>
                <strong>Temperature:</strong> {weather.main.temp}°C
              </p>
              <p>
                <strong>Humidity:</strong> {weather.main.humidity}%
              </p>
              <p>
                <strong>Wind Speed:</strong> {weather.wind.speed} m/s
              </p>
              <p>
                <strong>Condition:</strong> {weather.weather[0].description}
              </p>
            </div>
          )}
        </div>

        {/* Right Section: Additional Content */}
        <div style={{ flex: "1", padding: "10px" }}>
          <div
            style={{
              backgroundColor: "white",
              padding: "20px",
              borderRadius: "5px",
              boxShadow: "0px 0px 10px rgba(0,0,0,0.1)",
            }}
          >
            <h3>Additional Features</h3>
            <p>Stay tuned for more features like:</p>
            <ul>
              <li>5-day Forecast</li>
              <li>Interactive Radar</li>
              <li>City Maps</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer
        style={{
          textAlign: "center",
          padding: "10px",
          backgroundColor: "#007BFF",
          color: "white",
        }}
      >
        <p>&copy; 2024 Weather App. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default WeatherApp;
