import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [cityImage, setCityImage] = useState("");
  const [error, setError] = useState("");
  const [isCelsius, setIsCelsius] = useState(true);

  const fetchCityImage = async (cityName) => {
    try {
      const unsplashAccessKey = "S34fXCk3kF4x2wGTpEixKLVkVZWwtEcsh4lojX3Ubko";
      const response = await axios.get(
        `https://api.unsplash.com/search/photos`,
        {
          params: {
            query: `${cityName} skyline`,
            client_id: unsplashAccessKey,
            per_page: 1,
            orientation: "landscape",
          },
        }
      );
      setCityImage(response.data.results[0]?.urls.regular || "");
    } catch (err) {
      setCityImage("");
      console.error("Error fetching city image:", err);
    }
  };

  const fetchWeather = async () => {
    try {
      setError("");
      setWeather(null);
      setCityImage("");
      const apiKey = "79b5cdad33a13f8b96ec23e71c4b0930";
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather`,
        {
          params: {
            q: city,
            appid: apiKey,
            units: "metric",
          },
        }
      );
      setWeather(response.data);
      fetchCityImage(city);
    } catch (err) {
      setError("City not found. Please try again.");
      console.error("Error fetching weather data:", err);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
  };

  const getWeatherCondition = (main) => {
    const conditions = {
      Clear: "Clear sky",
      Clouds: "Cloudy",
      Rain: "Rainy",
      Drizzle: "Drizzle",
      Thunderstorm: "Thunderstorm",
      Snow: "Snowy",
      Mist: "Misty",
      Haze: "Hazy",
    };
    return conditions[main] || main;
  };

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        minHeight: "100vh",
        color: "white",
        backgroundImage: "url('https://source.unsplash.com/1600x900/?weather')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundBlendMode: "overlay",
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        padding: "20px",
      }}
    >
      <h1
        style={{ textAlign: "center", fontSize: "3rem", marginBottom: "20px" }}
      >
        Weather App
      </h1>
      <div
        style={{
          marginBottom: "20px",
          textAlign: "center",
        }}
      >
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={{
            padding: "10px",
            marginRight: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
            width: "200px",
          }}
        />
        <button
          onClick={fetchWeather}
          style={{
            padding: "10px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            transition: "background-color 0.3s",
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#0056b3")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#007BFF")}
        >
          Get Weather
        </button>
      </div>
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
      {weather && (
        <div
          style={{
            display: "flex", // Flexbox for layout
            justifyContent: "center",
            alignItems: "flex-start",
            margin: "20px auto",
            padding: "20px",
            border: "1px solid #ccc",
            borderRadius: "10px",
            maxWidth: "800px",
            backgroundColor: "rgba(255, 255, 255, 0.9)",
            color: "#333",
          }}
        >
          <div style={{ flex: "1", marginRight: "20px" }}>
            <h2 style={{ textAlign: "center" }}>Weather in {weather.name}</h2>
            <img
              src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
              alt={weather.weather[0].description}
              style={{
                width: "100px",
                height: "100px",
                display: "block",
                margin: "0 auto",
              }}
            />
            <p>
              <strong>Temperature:</strong>{" "}
              {isCelsius
                ? `${weather.main.temp}째C`
                : `${(weather.main.temp * 9) / 5 + 32}째F`}
            </p>
            <p>
              <strong>Humidity:</strong> {weather.main.humidity}%
            </p>
            <p>
              <strong>Wind Speed:</strong> {weather.wind.speed} m/s
            </p>
            <p>
              <strong>Condition:</strong>{" "}
              {getWeatherCondition(weather.weather[0].main)}
            </p>
            <p>
              <strong>Description:</strong> {weather.weather[0].description}
            </p>
            <p>
              <strong>Sunrise:</strong> {formatTime(weather.sys.sunrise)}
            </p>
            <p>
              <strong>Sunset:</strong> {formatTime(weather.sys.sunset)}
            </p>
          </div>
          {cityImage && (
            <div style={{ flex: "1" }}>
              <img
                src={cityImage}
                alt={`A view of ${city}`}
                style={{
                  width: "100%",
                  height: "auto",
                  borderRadius: "10px",
                  border: "1px solid #ccc",
                }}
              />
            </div>
          )}
        </div>
      )}
      <div style={{ textAlign: "center", marginTop: "20px" }}>
        <button
          onClick={() => setIsCelsius(!isCelsius)}
          style={{
            padding: "10px 20px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            transition: "background-color 0.3s",
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#0056b3")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#007BFF")}
        >
          Switch to {isCelsius ? "Fahrenheit" : "Celsius"}
        </button>
      </div>
      <footer
        style={{ textAlign: "center", marginTop: "40px", color: "white" }}
      >
        <p>&copy; 2024 Weather App. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default WeatherApp;
