import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [city, setCity] = useState(""); // User-entered city
  const [weather, setWeather] = useState(null); // Weather data
  const [cityImage, setCityImage] = useState(""); // City image URL
  const [error, setError] = useState(""); // Error message

  // Fetch city image
  const fetchCityImage = async (cityName) => {
    try {
      const unsplashAccessKey = "S34fXCk3kF4x2wGTpEixKLVkVZWwtEcsh4lojX3Ubko"; // Unsplash Access Key
      const response = await axios.get(
        `https://api.unsplash.com/search/photos`,
        {
          params: {
            query: `${cityName} skyline`, // More specific keyword
            client_id: unsplashAccessKey,
            per_page: 1, // Fetch one image
            orientation: "landscape", // Prefer landscape images
          },
        }
      );
      if (response.data.results.length > 0) {
        setCityImage(response.data.results[0].urls.regular); // Set image URL
      } else {
        setCityImage(""); // Clear image URL if no image found
      }
    } catch (err) {
      setCityImage(""); // Clear image URL on error
      console.error("Error fetching city image:", err);
    }
  };

  // Fetch weather data
  const fetchWeather = async () => {
    try {
      setError("");
      setWeather(null); // Reset weather data
      setCityImage(""); // Clear previous image
      const apiKey = "79b5cdad33a13f8b96ec23e71c4b0930"; // OpenWeatherMap API key
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather`,
        {
          params: {
            q: city, // City name
            appid: apiKey,
            units: "metric", // Celsius temperature
          },
        }
      );
      setWeather(response.data);
      fetchCityImage(city); // Fetch city image
    } catch (err) {
      setError("City not found. Please try again."); // Error message
      console.error("Error fetching weather data:", err);
    }
  };

  // Map weather condition to more user-friendly descriptions
  const getWeatherCondition = (main) => {
    const conditions = {
      Clear: "Clear sky",
      Clouds: "Cloudy",
      Rain: "Rainy",
      Drizzle: "Drizzle",
      Thunderstorm: "Thunderstorm",
      Snow: "Snowy",
      Mist: "Misty",
      Haze: "Hazy",
    };
    return conditions[main] || main; // Return original description if not mapped
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h1>Weather App</h1>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={{
            padding: "10px",
            marginRight: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        />
        <button
          onClick={fetchWeather}
          style={{
            padding: "10px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Get Weather
        </button>
      </div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {weather && (
        <div
          style={{
            marginTop: "20px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        >
          <h2>Weather in {weather.name}</h2>
          <img
            src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt={weather.weather[0].description}
            style={{ width: "100px", height: "100px" }}
          />
          <p>
            <strong>Temperature:</strong> {weather.main.temp}Â°C
          </p>
          <p>
            <strong>Humidity:</strong> {weather.main.humidity}%
          </p>
          <p>
            <strong>Wind Speed:</strong> {weather.wind.speed} m/s
          </p>
          <p>
            <strong>Condition:</strong>{" "}
            {getWeatherCondition(weather.weather[0].main)}
          </p>
          <p>
            <strong>Description:</strong> {weather.weather[0].description}
          </p>
        </div>
      )}
      {cityImage && (
        <div>
          <img
            src={cityImage}
            alt={`A view of ${city}`}
            style={{
              width: "100%",
              height: "auto",
              marginTop: "20px",
              borderRadius: "5px",
            }}
          />
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
