import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [cityImage, setCityImage] = useState("");
  const [error, setError] = useState("");

  const fetchCityImage = async (cityName) => {
    try {
      const unsplashAccessKey = "S34fXCk3kF4x2wGTpEixKLVkVZWwtEcsh4lojX3Ubko";
      const response = await axios.get(
        `https://api.unsplash.com/search/photos`,
        {
          params: {
            query: `${cityName} skyline`,
            client_id: unsplashAccessKey,
            per_page: 1,
            orientation: "landscape",
          },
        }
      );
      setCityImage(response.data.results[0]?.urls.regular || "");
    } catch (err) {
      setCityImage("");
      console.error("Error fetching city image:", err);
    }
  };

  const fetchWeather = async () => {
    try {
      setError("");
      setWeather(null);
      setCityImage("");
      const apiKey = "79b5cdad33a13f8b96ec23e71c4b0930";
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather`,
        {
          params: {
            q: city,
            appid: apiKey,
            units: "metric",
          },
        }
      );
      setWeather(response.data);
      fetchCityImage(city);
    } catch (err) {
      setError("City not found. Please try again.");
      console.error("Error fetching weather data:", err);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
  };

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        minHeight: "100vh",
        backgroundColor: "#f4f4f4",
      }}
    >
      {/* 1. 添加顶部导航栏 */}
      <nav
        style={{
          backgroundColor: "#007BFF",
          padding: "10px",
          color: "white",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <h1 style={{ margin: "0", fontSize: "1.5rem" }}>Weather App</h1>
        <div>
          <a
            href="#"
            style={{
              color: "white",
              margin: "0 10px",
              textDecoration: "none",
              fontSize: "1rem",
            }}
          >
            Home
          </a>
          <a
            href="#"
            style={{
              color: "white",
              margin: "0 10px",
              textDecoration: "none",
              fontSize: "1rem",
            }}
          >
            Forecast
          </a>
          <a
            href="#"
            style={{
              color: "white",
              margin: "0 10px",
              textDecoration: "none",
              fontSize: "1rem",
            }}
          >
            Radar
          </a>
        </div>
      </nav>

      {/* 2. 搜索输入框背景 */}
      <div
        style={{
          textAlign: "center",
          padding: "40px",
          backgroundImage:
            "url('https://source.unsplash.com/1600x900/?clouds')",
          backgroundSize: "cover",
          color: "white",
        }}
      >
        <h2>Search Weather by City</h2>
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={{
            padding: "10px",
            marginRight: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <button
          onClick={fetchWeather}
          style={{
            padding: "10px 20px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Get Weather
        </button>
      </div>

      {/* 3. 分段显示内容 */}
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
      {weather && (
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "center",
            alignItems: "flex-start",
            margin: "20px auto",
            padding: "20px",
            borderRadius: "10px",
            backgroundColor: "white",
            maxWidth: "1200px",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
          }}
        >
          {/* 左侧：天气信息 */}
          <div style={{ flex: "2", marginRight: "20px" }}>
            <h2 style={{ textAlign: "center" }}>Weather in {weather.name}</h2>
            <p>
              <strong>Temperature:</strong> {weather.main.temp}°C
            </p>
            <p>
              <strong>Humidity:</strong> {weather.main.humidity}%
            </p>
            <p>
              <strong>Wind Speed:</strong> {weather.wind.speed} m/s
            </p>
            <p>
              <strong>Condition:</strong> {weather.weather[0].description}
            </p>
            <p>
              <strong>Sunrise:</strong> {formatTime(weather.sys.sunrise)}
            </p>
            <p>
              <strong>Sunset:</strong> {formatTime(weather.sys.sunset)}
            </p>
          </div>

          {/* 右侧：城市图片 */}
          {cityImage && (
            <div style={{ flex: "1" }}>
              <img
                src={cityImage}
                alt={`View of ${city}`}
                style={{
                  width: "100%",
                  height: "auto",
                  borderRadius: "10px",
                  boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
                }}
              />
            </div>
          )}
        </div>
      )}

      {/* 4. 页面分区：未来几天天气预报（占位） */}
      <div
        style={{
          marginTop: "20px",
          padding: "20px",
          textAlign: "center",
          backgroundColor: "#007BFF",
          color: "white",
        }}
      >
        <h3>Weekly Forecast (Coming Soon)</h3>
        <p>More features will be added here soon!</p>
      </div>

      {/* Footer */}
      <footer
        style={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#333",
          color: "white",
        }}
      >
        <p>&copy; 2024 Weather App. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default WeatherApp;
