import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [city, setCity] = useState(""); // 用户输入的城市
  const [weather, setWeather] = useState(null); // API 返回的天气数据
  const [error, setError] = useState(""); // 错误信息

  const fetchWeather = async () => {
    try {
      setError(""); // 清空错误信息
      setWeather(null); // 清空之前的天气数据
      const apiKey = "79b5cdad33a13f8b96ec23e71c4b0930"; // OpenWeatherMap API 密钥
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather`,
        {
          params: {
            q: city,
            appid: apiKey,
            units: "metric", // 摄氏温度单位
          },
        }
      );
      console.log("API Response:", response.data); // 调试用：打印 API 返回数据
      setWeather(response.data); // 更新天气数据
    } catch (err) {
      console.error("Error fetching weather data:", err); // 打印错误信息
      if (err.response && err.response.status === 404) {
        setError("City not found. Please try again."); // 城市未找到
      } else if (err.response && err.response.status === 401) {
        setError("Invalid API key. Please check your API configuration."); // API 密钥无效
      } else {
        setError("An unexpected error occurred. Please try again later."); // 其他错误
      }
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h1>Weather App</h1>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)} // 更新城市输入
          style={{
            padding: "10px",
            marginRight: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        />
        <button
          onClick={fetchWeather}
          style={{
            padding: "10px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Get Weather
        </button>
      </div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {weather && (
        <div
          style={{
            marginTop: "20px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        >
          <h2>Weather in {weather.name}</h2>
          <p>
            <strong>Temperature:</strong> {weather.main.temp}°C
          </p>
          <p>
            <strong>Humidity:</strong> {weather.main.humidity}%
          </p>
          <p>
            <strong>Description:</strong> {weather.weather[0].description}
          </p>
          <p>
            <strong>Wind Speed:</strong> {weather.wind.speed} m/s
          </p>
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
